import React ,{Fragment,useEffect } from 'react'
import GetComponent from './components/GetComponent';
import PostComponent from './components/PostComponent';
import { Route,Routes,Link,Outlet } from 'react-router-dom';
import { Disclosure, Menu, Transition } from '@headlessui/react'
import { BellIcon, MenuIcon, XIcon } from '@heroicons/react/outline'
import useToken from "./components/Hooks/useToken";

const App = () =>{


  const urlSearchParams = new URLSearchParams(window.location.hash);
  const params = Object.fromEntries(urlSearchParams.entries());
  const API_URL = process.env.REACT_APP_SERVER_URL;    
  const LOGIN =process.env.REACT_APP_LOGIN_URL;
  const { Token, setToken } = useToken();


  if(Token == null || Token == "") {
    if(params["#id_token"] != undefined){
       setToken(params["#id_token"])
    }
 }
 
    useEffect(() => {
    
      var TokenValue = Token.value ? Token.value : Token;

     /*  axios.get(API_URL+'/decodetoken', {
      params: {
          encoded:'"'+TokenValue+'"'
      }
          }).then(response => { 

            var dataResult = JSON.parse(JSON.stringify(response.data));    
            console.log(dataResult)  
            if(dataResult.errorMessage !== undefined ){
                if(dataResult.errorMessage){
                  swal({  
                      title:"Error",
                      text:"Not authorized",
                      icon: "error"
                    }).then(() => {
                      window.localStorage.clear();
                      window.location.href = LOGIN; 

                    });


                }
            }else{

                //entro
              }
                  
          
          })  */
            
        }, [Token]);

  function classNames(...classes) {
    return classes.filter(Boolean).join(' ')
  }

  const navigation = [
    { name: 'Get', href: '/get', current: true },
    { name: 'Post', href: '/post', current: false }
  ]
  return (
    <div>
      
      <Disclosure as="nav" className="bg-gray-800">
      {({ open }) => (
        <>
          <div className="max-w-7xl mx-auto px-2 sm:px-6 lg:px-8">
            <div className="relative flex items-center justify-between h-16">
              <div className="absolute inset-y-0 left-0 flex items-center sm:hidden">
                {/* Mobile menu button*/}
                <Disclosure.Button className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-white">
                  <span className="sr-only">Open main menu</span>
                  {open ? (
                    <XIcon className="block h-6 w-6" aria-hidden="true" />
                  ) : (
                    <MenuIcon className="block h-6 w-6" aria-hidden="true" />
                  )}
                </Disclosure.Button>
              </div>
              <div className="flex-1 flex items-center justify-center sm:items-stretch sm:justify-start">
                <div className="flex-shrink-0 flex items-center">
                  <img
                    className="block lg:hidden h-8 w-auto"
                    src="https://tailwindui.com/img/logos/workflow-mark-indigo-500.svg"
                    alt="Workflow"
                  />
                  <img
                    className="hidden lg:block h-8 w-auto"
                    src="https://tailwindui.com/img/logos/workflow-logo-indigo-500-mark-white-text.svg"
                    alt="Workflow"
                  />
                </div>
                <div className="hidden sm:block sm:ml-6">
                  <div className="flex space-x-4">
                    {navigation.map((item) => (
                      <Link to={item.href}>
                        <a
                        key={item.name}                     
                        className={classNames(
                          item.current ? 'bg-gray-900 text-white' : 'text-gray-300 hover:bg-gray-700 hover:text-white',
                          'px-3 py-2 rounded-md text-sm font-medium'
                        )}
                        aria-current={item.current ? 'page' : undefined}
                      >
                        {item.name}
                      </a>
                      </Link>
                    
                    ))}
                  </div>
                </div>
              </div>
           
            </div>
          </div>

          <Disclosure.Panel className="sm:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1">
              {navigation.map((item) => (
                <Disclosure.Button
                  key={item.name}
                  as="a"
                  href={item.href}
                  className={classNames(
                    item.current ? 'bg-gray-900 text-white' : 'text-gray-300 hover:bg-gray-700 hover:text-white',
                    'block px-3 py-2 rounded-md text-base font-medium'
                  )}
                  aria-current={item.current ? 'page' : undefined}
                >
                  {item.name}
                </Disclosure.Button>
              ))}
            </div>
          </Disclosure.Panel>
        </>
      )}
    </Disclosure>



      <Outlet/>



      <Routes>
        <Route path="/get" element={<GetComponent />} ></Route>
        <Route path="/post" element={<PostComponent />} ></Route>

      </Routes>
    </div>



  )
}

export default App;
